REPLACE INTO publications(url,pdf,year,authors,journal,title,spage,epage,issue,abstract,guid,issn) values("http://www.guihaia-journal.com/ch/reader/view_abstract.aspx?file_no=198204002&flag=1","http://www.guihaia-journal.com/ch/reader/create_pdf.aspx?file_no=198204002&flag=1&year_id=1982&quarter_id=4","1982","谢福惠","广西植物","从木材解剖特征初拟针叶树分类系统","175","180","4","针叶树(Conifers)包括银杏纲和松杉纲的树种。根据《中国植物志》第七卷记载,现代裸子植物的种类分属于4纲9目12科71属约800种,我国产4纲8目11科41属236种,栽培1科7属51种。从国产针叶树材的解剖特征观察,银杏纲木材构造简单,仅具管胞、含晶细胞和射线细胞。管胞排列不整齐,射线薄壁细胞垂直壁无节状加厚,四隅无凹痕,轴向薄壁细胞缺,射线仅单例,所以是针叶村最原始的一个纲,应排列在松杉纲之前。","http://www.guihaia-journal.com/ch/reader/view_abstract.aspx?file_no=198204002&flag=1","1000-3142");
