REPLACE INTO multilingual(`key`,language,value,guid) values("abstract","zh","灵香草的斑枯病俗称“点蜡烛”病,历来是灵香草的毁灭性病害。此病分布于广西金秀县各个灵香草产区;融水、龙胜等灵香草产地亦有此病发生。一般发病率60～70％,严重的往往达80％～90％,甚至失收。如1980年8月,金秀县外贸局为扶植生产队发展灵香草生产,无偿拨给金秀二队种苗300斤,分给社员种植。灵香草生长到高约23—27厘米时,全部病死腐烂。因此,要发展灵香草生产,必须有效地防治这种病害。","http://www.guihaia-journal.com/ch/reader/view_abstract.aspx?file_no=198303012&flag=1");
REPLACE INTO publications(pdf,year,authors,journal,title,spage,epage,issue,abstract,guid,issn) values("http://www.guihaia-journal.com/ch/reader/create_pdf.aspx?file_no=198303013&flag=1&year_id=1983&quarter_id=3","1983","黄演濂","广西植物","中华猕猴桃大果型优株79—2的初步选育","219","222","3","我园于1978—1981年对赣北地区(瑞昌、庐山、德安、武宁、修水、永修、湖口、都昌、星子等县),进行了为期4年的资源调查和单株选优工作。在优株78—1、78—2、79—1、79—2、80—1、81—5—1之中选出最优的一株79—2系中华猕猴桃软毛变种(Actinidia chinensis var. chinensis),于1980年3月18日进行嫁接,把优株上的芽体嫁接在本园栽培的一年生砧木上,观察其优良的经济性状是否能够保持。经过1980—1982年8年的培育、观察,初步证明79—2确是一个价值很高,值得推广的优良株系。","http://www.guihaia-journal.com/ch/reader/view_abstract.aspx?file_no=198303013&flag=1","1000-3142");
