REPLACE INTO multilingual(`key`,language,value,guid) values("abstract","zh","梧桐科是锦葵目的一个多型的科,约有68属1100种,主要分布在热带、亚热带地区,只有极少数种类可分布到温带地区。 中国梧桐科植物,连栽培的种类在内,共有22属87种3变种,其分布范围一般不超过长江以北,并以北回归线以南分布最盛,只梧桐(Firmiana simplex(Linn.)F. W. Wight)可栽培至华北和西北各省。计有: 苹婆属(Sterculia)23种1变种;翅苹婆属(Pterygota)1种;梧桐属(Firmiana)3种;火桐属(Erythropsis)3种;胖大海属(Scaphium)2种(栽培);银叶树属(Heritiera)3种;滇桐属(Craigia)2种;鹧鸪麻属(Kleinhovia)1种;梭罗树属(Reevesia)14种,山芝麻属(Helicteres)g种;","http://www.guihaia-journal.com/ch/reader/view_abstract.aspx?file_no=198402010&flag=1");
REPLACE INTO publications(url,pdf,year,authors,journal,title,spage,epage,issue,abstract,guid,issn) values("http://www.guihaia-journal.com/ch/reader/view_abstract.aspx?file_no=198402011&flag=1","http://www.guihaia-journal.com/ch/reader/create_pdf.aspx?file_no=198402011&flag=1&year_id=1984&quarter_id=2","1984","戴策刚 谭文澄","广西植物","广西蜜源植物概况","157","170","2","本文扼要介绍了广西的地理位置与气候特点,简述了广西富饶的蜜源植物对养蜂生产的意义,将分属于100余科内的360余种蜜源植物,整理成表格形式。内容包括科名、种名、花期、蜜粉量和该植物在广西的分布等,主要蜜源植物27种,注以“*”号,有毒蜜源植物4种,以文字注明。","http://www.guihaia-journal.com/ch/reader/view_abstract.aspx?file_no=198402011&flag=1","1000-3142");
