REPLACE INTO publications(title,journal,issn,year,issue,spage,epage,abstract,url,authors,guid) values("广西西江流域植被移除磷素污染的生态系统服务价值","广西植物","1000-3142","2013","1",52,57,"3S 技术的应用,为快速计算生态系统服务价值提供了新的方法.以 ArcGIS9.2为平台,构建广西西江流域生态系统减轻集水区出口受纳水体非点源污染服务物质量和价值量评估模型,以总磷(T P )作为指示污染物,模拟2008年流域内每个栅格像元被植被移除而未进入水环境的污染物质量空间分布特征,结合期望水质标准和净化污染物的边际成本计算出服务的价值.结果表明:西江流域的水文敏感区主要集中在地势平缓的区域;磷素关键污染源区分布与水文敏感区相近,主要位于桂林、柳州、南宁、来宾及贵港.当年被西江流域生态系统过滤移除而未进入水环境的磷素污染物质总量达2673.36t ・ a‐1,占关键污染源区指数总量的73畅2%,其中林地生态系统服务价值贡献率最高.流域生态系统对于减轻其出口受纳水体磷素非点源污染服务的总价值为3462.44万元.","http://d.wanfangdata.com.cn/Periodical/gxzw201301010","徐靖;银森录;吴楠;李俊生*;孙光","http://d.wanfangdata.com.cn/Periodical/gxzw201301010");
