REPLACE INTO publications(title,journal,issn,year,issue,spage,epage,abstract,url,authors,guid) values("中国兰科毛兰属一新记录种--孟连毛兰","广西植物","1000-3142","2013","1",70,71,"报道了中国兰科毛兰属一记录种———孟连毛兰,本种主要特点是花序短且一般具2朵花,花梗基部具较大的花苞片,唇瓣上有2条形状各异、增厚的附属物.但其茎长达30 cm ,花梗和子房、花萼背面光滑无柔毛,唇瓣侧裂片大于中裂片等特征可与棒茎毛兰明显区别开.","http://d.wanfangdata.com.cn/Periodical/gxzw201301013","刘强;李剑武;殷建涛;谭运洪;周仕顺;文彬;黄文;周哲昆","http://d.wanfangdata.com.cn/Periodical/gxzw201301013");
