REPLACE INTO publications(title,journal,issn,year,volume,issue,spage,epage,abstract,url,doi,authors,guid) values("锈革菌属两个中国新记录种","广西植物","1000-3142","2012","32","1",19,22,"报道了锈革菌属Hymenochaete两个中国新记录种:长孢锈革菌H.longispora采自广东省,主要特点是孢子长圆柱形且刚毛较大；微孢锈革菌H.nanospora采自海南省和广东省,主要特点是刚毛和孢子非常小.文中提供了这两个种的野外标本照片、显微结构绘图以及详细的形态学描述.","http://d.wanfangdata.com.cn/Periodical/gxzw201201006","10.3969/j.issn.1000-3142.2012.01.006","何双辉;李海蛟;HE Shuang-Hui;LI Hai-Jiao","10.3969/j.issn.1000-3142.2012.01.006");
