REPLACE INTO publications(title,journal,issn,year,volume,issue,spage,epage,abstract,url,doi,authors,guid) values("不同品种红花营养器官解剖结构及孢粉特征研究","广西植物","1000-3142","2012","32","1",46,52,"利用光学显微镜和扫描电镜技术对5个品种红花的花粉微形态特征和营养器官解剖特征进行比较研究.结果表明:5个品种红花花粉外壁突起为刺突和乳突两种,花粉表面均具有皱波和网状雕纹；不同品种红花的叶片角质层厚度、叶脉厚、叶厚、VPD指数、栅栏组织/海绵组织厚度比、根木射线条数差异均达到显著水平；茎中机械组织发达,维管束环状排列、维管束的排列数目上表现种间特异性.这说明5个品种红花孢粉及营养器官解剖结构可能具有一定的耐盐、耐旱的基本生态适应性.","http://d.wanfangdata.com.cn/Periodical/gxzw201201011","10.3969/j.issn.1000-3142.2012.01.011","高武军;沙涛;刘林;邓传良;卢龙斗;GAO Wu-Jun;SHA Tao;LIU Lin;DENG Chuan-Liang;LU Long-Dou","10.3969/j.issn.1000-3142.2012.01.011");
