REPLACE INTO publications(title,journal,issn,year,volume,issue,spage,epage,abstract,url,doi,authors,guid) values("山西五鹿山白皮松群落乔木层的种间关联性分析","广西植物","1000-3142","2012","32","1",63,68,"根据2&#215;2联列表,运用方差分析、x2检验、AC指数、Ochiai指数等技术,对山西五鹿山白皮松群落50个样方内的26种乔木的种间总体联结关系以及各种对间关联显著性和关联系数进行分析与测定.结果表明:该地区26种乔木的总体联结关系以正关联为主,表明白皮松林与其外环境是相适应的；种对间以正、负关联两种形式存在,成对物种之间正负关联的出现,是该群落内环境的表现,也是由群落内环境异质性所致.","http://d.wanfangdata.com.cn/Periodical/gxzw201201014","10.3969/j.issn.1000-3142.2012.01.014","王丽丽;毕润成;闫明;WANG Li-Li;BI Run-Cheng;YAN Ming","10.3969/j.issn.1000-3142.2012.01.014");
