REPLACE INTO publications(title,journal,issn,year,volume,issue,spage,epage,abstract,url,doi,authors,guid) values("基于ITS序列探讨珍珠菜属过路黄组的系统关系","广西植物","1000-3142","2012","32","2",163,166,"测定了紫脉过路黄、临时救和过路黄的nrDNA ITS序列,并分析了珍珠菜属过路黄组17个物种的遗传距离及亲缘关系.结果表明,过路黄组植物的ITS序列长度在620～628间,一致性高达90.59%,种间遗传距离为0.002～0.199.系统发育树表明:(1)紫脉过路黄、临时救和小茄亲缘关系较近;(2)大叶过路黄、落地梅和过路黄亲缘关系较近;(3)山萝过路黄、贯叶过路黄、管茎过路黄、叶头过路黄、峨眉过路黄及三角叶过路黄亲缘关系较近.ITS序列分析结果为组内植物的鉴定、分类及系统进化提供了新的参考.","http://d.wanfangdata.com.cn/Periodical/gxzw201202005","10.3969/j.issn.1000-3142.2012.02.005","郑伟;陈龙清;ZHENG Wei;CHEN Long-Qing","10.3969/j.issn.1000-3142.2012.02.005");
