REPLACE INTO publications(title,journal,issn,year,volume,issue,spage,epage,abstract,url,doi,authors,guid) values("中国板栗EST-SSR分子标记在栲树中的通用性分析","广西植物","1000-3142","2012","32","3",293,297,"简单重复序列也称为微卫星分子标记,不仅在同属近缘种间具有良好的通用性,甚至在近缘属间也具有一定的通用性.本研究利用壳斗科基因组信息数据库中公布的中国板栗124对多态的EST-SSR引物在栲树中进行跨属(栗属到栲属)通用性研究,结果显示中国板栗EST-SSR引物在栲树中通用性和多态性分别为42.7％和56.6％；使用19对多态的EST-SSR引物对4个栲树自然居群的遗传多样性进行初步分析,结果显示栲树自然居群具有较高的遗传多样性(Na=6.105,Ho=0.563,He=0.621).这些引物为栲树群体遗传学的深入研究提供了有力工具.","http://d.wanfangdata.com.cn/Periodical/gxzw201203002","10.3969/j.issn.1000-3142.2012.03.002","李春;孙晔;LI Chun;SUN Ye","10.3969/j.issn.1000-3142.2012.03.002");
