REPLACE INTO publications(title,journal,issn,year,volume,issue,spage,epage,abstract,url,doi,authors,guid) values("基于年轮分析的桂林岩溶区青冈栎地上生物量研究","广西植物","1000-3142","2012","32","4",464,467,"采用年轮分析法对岩溶区青冈栎样树年轮和生物量进行测定并对其群落生物量进行估算.研究结果显示,青冈栎平均树龄46 a,平均胸径20.52 cm,单位地上生物量为98.668 t&#183;hm-2.随着植株的生长,胸径越大则生物量越大,两者形成明显的幂函数关系.d、e、f、g和h样树地上生物量为2.884、22.567、108.183、134.314和379.799 kg,生物量分配状况是主干＞枝条＞树叶,大部分的生物量分配在主干,生物量随着胸径的增大明显加大.","http://d.wanfangdata.com.cn/Periodical/gxzw201204009","10.3969/j.issn.1000-3142.2012.04.009","潘复静;张中峰;黄玉清;莫凌;PAN Fu-Jing;ZHANG Zhong-Feng;HUANG Yu-Qing;MO Ling","10.3969/j.issn.1000-3142.2012.04.009");
