REPLACE INTO publications(title,journal,issn,year,volume,issue,spage,epage,abstract,url,doi,authors,guid) values("禺毛茛复合体及其近缘种多倍体分析","广西植物","1000-3142","2012","32","5",583,586,"利用细胞学及系统进化方法,分析了禺毛茛复合体及其近缘种的多倍体现象,以及多倍体对植物物种形成、形态、生态及生理等方面的影响.结果表明:禺毛茛复合体及其近缘种的多倍体现象突出,多倍体在形态上更为粗壮、生态分布上范围更大、生理上由于基因组的相容性水平提高而使得植株的适应性及抗逆性增强.","http://d.wanfangdata.com.cn/Periodical/gxzw201205003","10.3969/j.issn.1000-3142.2012.05.003","聂谷华;廖亮;NIE Gu-Hua;LIAO Liang","10.3969/j.issn.1000-3142.2012.05.003");
