REPLACE INTO publications(title,journal,issn,year,volume,issue,spage,epage,abstract,url,doi,authors,guid) values("荨麻科楼梯草属七种植物果实的补充描述","广西植物","1000-3142","2012","32","5",603,606,"楼梯草属植物的果实形态特征是属下系的划分和种类鉴定的重要依据之一,对于研究该属演化关系具有重要意义.通过标本查阅及野外调查,为该属七种植物的果实进行了补充描述,对该属的进一步分类修订提供有价值的信息.","http://d.wanfangdata.com.cn/Periodical/gxzw201205007","10.3969/j.issn.1000-3142.2012.05.007","吴增源;王红;李德铢;WU Zeng-Yuan;WANG Hong;LI De-Zhu","10.3969/j.issn.1000-3142.2012.05.007");
