REPLACE INTO publications(title,abstract,journal,issn,volume,issue,spage,epage,date,year,authors,pdf,url,guid) values("愛知県のムヨウラン類","愛知県には,5種2品種のムヨウラン類が生育している.ムヨウランとホクリクムヨウラン,ウスギムヨウランとエンシュウムヨウランは,同所的な生育地でも相互に識別可能であり,それぞれ種の階級で区別されるべきである.キイムヨウランはホクリクムヨウランの黄花品である.同じような黄花品は,エンシュウムヨウランにもある.","分類 : bunrui : 日本植物分類学会誌","1346-6852","5","1","33","38","2005-02-28","2005","芹沢 俊介","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0008350883","http://ci.nii.ac.jp/naid/110006342804","http://ci.nii.ac.jp/naid/110006342804");
