REPLACE INTO publications(title,abstract,journal,issn,volume,issue,spage,epage,date,year,authors,pdf,url,guid) values("平凡社刊「日本の野生植物シダ」の写真について","標準的に使われる図鑑の写真は,多くの人がそれを頼りに同定するため,影響が大きい.最近になって改めて「日本の野生植物 シダ」(平凡社)を検討したところ,相当数の写真に何らかの問題があるように思われた.そこで,利用者の注意を喚起する目的で,気にかかる写真のリストを作成し,その理由を記述した.","分類 : bunrui : 日本植物分類学会誌","1346-6852","7","1","31","37","2007-02-20","2007","芹沢 俊介","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0008350940","http://ci.nii.ac.jp/naid/110006342833","http://ci.nii.ac.jp/naid/110006342833");
