REPLACE INTO publications(title,abstract,journal,issn,volume,issue,spage,epage,date,year,authors,pdf,url,guid) values("明治の植物標本 : 長野菊次郎の植物標本の発見","島根大学生物資源科学部生物科学科で,明治時代中期から大正時代初期に長野菊次郎によって収集・作成された植物〓葉標本1410余点が発見された.保存状態がきわめて良いことから,日本植物科学研究史的価値だけでなく,明治時代の植物相に関する比較研究にも貴重な資料である.","分類 : bunrui : 日本植物分類学会誌","1346-6852","13","2","109","117","2013-08-26","2013","林 蘇娟;初見 真知子","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0010131799","http://ci.nii.ac.jp/naid/110009657361","http://ci.nii.ac.jp/naid/110009657361");
