REPLACE INTO publications(title,abstract,journal,issn,volume,issue,spage,epage,date,year,authors,pdf,url,guid) values("珪藻 Placoneis flabellata (F.Meister) Kimura, H.Fukush. et Ts.Kobay. comb. nov.の分類学的研究","2009年8月,スラウェシ島(インドネシア)Batimurung自然保護区の川の岸に繁茂したアオミドロと底泥からNavicula flabellata F.Meisterを大量に得た.光学顕微鏡で葉緑体と珪殻の形態構造,電子顕微鏡で縦溝などの形態を観察した結果,葉緑体の形,縦溝の形はPlaconeis型であることが分かった.したがって,本種はPlaconeis flabellata (F.Meister) Kimura, H.Fukush. et Ts.Kobay. comb. nov. (Basionym: Navicula flabellata F.Meister)とすべきである.","分類 : bunrui : 日本植物分類学会誌","1346-6852","15","2","125","136","2015-10-14","2015","木村 努;福島 博;小林 艶子","http://ci.nii.ac.jp/lognavi?name=nels&lang=en&type=pdf&id=ART0010575779","http://ci.nii.ac.jp/naid/110010014715","http://ci.nii.ac.jp/naid/110010014715");
