	//$h = get($url);
	
for ($year = 1992; $year < 2016; $year++)
{
	$url = 'http://www.zin.ru/journals/zsr/content.asp?year=' . $year;
	
	$h = get($url);
	
	if ($h != '')
	{

		$html = str_get_html($h);

		

		
		
		$papers = $html->find('div[class=paper]');

		foreach ($papers as $paper)
		{
		
			$guid = '';
			$pdf = '';

			$keys = array();
			$values = array();
		
			$title = $paper->find('p[class=title]');
			foreach ($title as $t)
			{
				//echo $t->plaintext . "\n";
				
				if (preg_match('/(?<authorstring>.*)\.\s+(?<year>[0-9]{4})\.\s+(?<title>.*)[\.|\?]\s+(?<journal>Zoosystematica Rossica),\s+(?<volume>\d+)\((?<issue>\d+)\):\s+(?<spage>\d+)([-|–](?<epage>\d+))?\./Uu', $t->plaintext, $m))
				{
					//print_r($m);
					
					foreach ($m as $k => $v)
					{
						if (!is_numeric($k) && ($v != ''))
						{
							//echo $k . "\n";
							switch ($k)
							{
								
								case 'authorstring':
									$authorstring = $v;
									$authorstring = str_replace(' & ', ',', $authorstring);
									$authorstring = str_replace(', ', ',', $authorstring);
									$authors = explode(',', $authorstring);

									$keys[] = 'authors';
									$values[] = '"' . addcslashes(join(';', $authors), '"') . '"';
									break;
								
								default:
									$keys[] = $k;
									$values[] = '"' . addcslashes($v, '"') . '"';
									
									break;
							}
						}
					}
				}
				
			}

			$title = $paper->find('div[class=abstract]');
			foreach ($title as $t)
			{
				$keys[] = 'abstract';
				$values[] = '"' . addcslashes($t->plaintext, '"') . '"';
			}

/*			
<td class=\'pdf_link\'><a target=\'_blank\' href=\'content/2010/zr_2010_19_2_Reshchikov.pdf\'>		
*/
			$title = $paper->find('td[class=pdf_link] a');
			foreach ($title as $t)
			{
				$keys[] = 'pdf';
				
				$pdf = 'http://www.zin.ru/journals/zsr/content/' . $t->href;
				$values[] = '"' . addcslashes($pdf, '"') . '"';
				
				$guid = $pdf;
			}
	
			
			//echo $paper->plaintext;
			
			$keys[] = 'issn';
			$values[] = '"2410-0226"';

			if ($guid == '')
			{	
				$guid = md5(join('', $values));
			}

			$keys[] = 'guid';
			$values[] = '"' . addcslashes($guid, '"') . '"';
			
			
			//print_r($keys);
			//print_r($values);
			
			$sql = 'REPLACE INTO publications(' . join(',', $keys) . ') values('
				. join(',', $values) . ');';
			echo $sql . "\n";
			
			

		}
	}

}


